  return (
    <div className={`rating ${sizeClass}`}>
      {[1, 2, 3, 4, 5].map((star) => (
        <span
          key={star}
          className={`star ${star <= rating ? 'filled' : ''} ${
            interactive ? 'cursor-pointer hover:scale-110' : ''
          } transition-transform`}
          onClick={interactive && onRatingChange ? () => onRatingChange(star) : undefined}
        >
          â˜…
        </span>
      ))}
    </div>
  );
};

